    DECLARE var_ordreOrig INTEGER;
    DECLARE var_idPareOrig INTEGER;
    DECLARE var_ordreExist INTEGER;
    DECLARE var_tipusOrig VARCHAR(2000);

    -- segur que existeix, el busqeum nosaltres
